CREATE OR REPLACE FUNCTION com_obter_se_atualiza_tipo_faq ( nr_seq_tipo_faq_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w			bigint;
ie_atualiza_faq_w			varchar(1) := 'S';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	com_tipo_faq_perfil
where	nr_seq_tipo_faq	= nr_seq_tipo_faq_p;

if (qt_existe_w > 0) then
	begin
	select	'S'
	into STRICT	ie_atualiza_faq_w
	from	com_tipo_faq_perfil
	where	nr_seq_tipo_faq		= nr_seq_tipo_faq_p
	and	cd_perfil			= cd_perfil_p
	and	coalesce(ie_permite_atualizacao,'S')	= 'S'  LIMIT 1;
	exception
	when others then
		ie_atualiza_faq_w := 'N';
	end;
end if;

return ie_atualiza_faq_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
