CREATE OR REPLACE FUNCTION com_obter_se_cliente_ref_site ( nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(10);


BEGIN

select	coalesce(max('S'), 'N')
into STRICT	ie_retorno_w
from	com_cliente_ref_site ccrs
where	ccrs.nr_seq_cliente = nr_seq_cliente_p
and	clock_timestamp() between coalesce(ccrs.dt_inicio_vigencia, clock_timestamp()) and fim_dia(ccrs.dt_fim_vigencia);

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
