CREATE OR REPLACE FUNCTION com_obter_se_cnpj_ja_cliente ( nr_seq_cliente_p bigint, cd_cnpj_p text, ie_produto_p text) RETURNS varchar AS $body$
DECLARE


ie_cnpj_ja_cliente_w	varchar(1) := 'N';


BEGIN
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') and (cd_cnpj_p IS NOT NULL AND cd_cnpj_p::text <> '') and (ie_produto_p IS NOT NULL AND ie_produto_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_cnpj_ja_cliente_w
	from	com_cliente
	where	nr_sequencia <> nr_seq_cliente_p
	and	cd_cnpj = cd_cnpj_p
	and	ie_produto = ie_produto_p;
	end;
end if;
return ie_cnpj_ja_cliente_w;
end;
