CREATE OR REPLACE FUNCTION com_obter_se_gestor_vig ( cd_pessoa_fisica_p text, nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	com_cliente_gestor a
where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
and	a.nr_seq_cliente =  nr_seq_cliente_p
and	a.nr_sequencia = (SELECT max(b.nr_sequencia) from com_cliente_gestor b where a.nr_sequencia = b.nr_sequencia);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
