CREATE OR REPLACE FUNCTION com_obter_se_mostrar_mes_relat ( dt_mes_p timestamp, dt_ref_prev_term_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_mostrar_w	varchar(1) := 'S';


BEGIN
if (dt_mes_p IS NOT NULL AND dt_mes_p::text <> '') and (dt_ref_prev_term_p IS NOT NULL AND dt_ref_prev_term_p::text <> '') then
	begin
	if (trunc(dt_mes_p,'month') <= trunc(dt_ref_prev_term_p,'month')) then
		begin
		ie_mostrar_w := 'S';
		end;
	else
		begin
		ie_mostrar_w := 'N';
		end;
	end if;
	end;
end if;
return ie_mostrar_w;
end;
