SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_obter_se_prospect_ativo ( nr_seq_cliente_p bigint, nr_seq_canal_p bigint, dt_referencia_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
PA - Prospects abertos no período
A - Ativos por mês
CF - Cliente fechado no período
CA - Prospect aberto no período
*/
ds_retorno_w			varchar(1);
dt_inicial_prospect_w		timestamp;
dt_final_prospect_w		timestamp;
dt_inicio_canal_w		timestamp;
nr_seq_canal_w			bigint;
dt_fechamento_cliente_w		timestamp;
dt_abertura_canal_w		timestamp;
dt_fechamento_canal_w		timestamp;


BEGIN

ds_retorno_w := 'N';

/* Data inicial do prospect */

select	max(a.dt_log)
into STRICT	dt_inicial_prospect_w
from	com_cliente_log a
where	a.ie_log		= 2
and	a.ie_classificacao	= 'P'
and	a.nr_seq_cliente	= nr_seq_cliente_p;

/* Data final do prospect */

select	coalesce(max(a.dt_log),clock_timestamp())
into STRICT	dt_final_prospect_w
from	com_cliente_log a
where	a.ie_log		= 2
and	coalesce(a.dt_log,clock_timestamp())	>= dt_inicial_prospect_w
and	a.ie_classificacao	<> 'P'
and	a.nr_seq_cliente	= nr_seq_cliente_p;

/* Data de abertura do canal */

select	max(a.dt_log)
into STRICT	dt_abertura_canal_w
from	com_cliente_log a
where	a.nr_seq_canal	= nr_seq_canal_p
and	a.ie_log	= 3
and	a.nr_seq_cliente	= nr_seq_cliente_p;

if (coalesce(dt_abertura_canal_w::text, '') = '') then
	select	max(a.dt_inicio_atuacao)
	into STRICT	dt_abertura_canal_w
	from	com_canal_cliente a
	where	a.nr_seq_canal	= nr_seq_canal_p
	and	a.nr_seq_cliente	= nr_seq_cliente_p;
end if;

/* Data de fechamento do canal */

select	max(a.dt_log)
into STRICT	dt_fechamento_canal_w
from	com_cliente_log a
where	a.nr_seq_canal	= nr_seq_canal_p
and	a.ie_log	= 4
and	a.nr_seq_cliente	= nr_seq_cliente_p;

if (coalesce(dt_fechamento_canal_w::text, '') = '') then
	select	coalesce(max(a.dt_fim_atuacao),clock_timestamp())
	into STRICT	dt_fechamento_canal_w
	from	com_canal_cliente a
	where	a.nr_seq_canal	= nr_seq_canal_p
	and	a.nr_seq_cliente	= nr_seq_cliente_p;
end if;

/* Data de fechamento com o cliente*/

select	max(a.dt_log)
into STRICT	dt_fechamento_cliente_w
from	com_cliente_log a
where	a.ie_log		= 2
and	a.ie_classificacao	= 'C'
and	a.nr_seq_cliente	= nr_seq_cliente_p;

if (ie_opcao_p	= 'A') then
	if (dt_referencia_p between trunc(dt_inicial_prospect_w,'month') and fim_mes(dt_final_prospect_w)) and (dt_referencia_p between trunc(dt_abertura_canal_w,'month') and fim_mes(dt_fechamento_canal_w)) then
		ds_retorno_w	:= 'S';
	end if;
elsif (ie_opcao_p	= 'PA') then
	if (trunc(dt_inicial_prospect_w,'month') between trunc(dt_referencia_p,'month') and trunc(dt_final_p,'month')) and (trunc(dt_inicial_prospect_w,'dd') between trunc(dt_abertura_canal_w,'dd') and trunc(dt_fechamento_canal_w,'dd')) then
		ds_retorno_w	:= 'S';
	end if;
elsif (ie_opcao_p	= 'CF') then
	if (dt_fechamento_cliente_w IS NOT NULL AND dt_fechamento_cliente_w::text <> '') and (trunc(dt_fechamento_cliente_w,'dd') between trunc(dt_referencia_p,'dd') and trunc(dt_final_p,'dd')) and (trunc(dt_fechamento_cliente_w,'dd') between trunc(dt_abertura_canal_w,'dd') and trunc(dt_fechamento_canal_w,'dd')) then
		ds_retorno_w	:= 'S';
	end if;
elsif (ie_opcao_p	= 'CA') then
	if (dt_inicial_prospect_w IS NOT NULL AND dt_inicial_prospect_w::text <> '') and (trunc(dt_inicial_prospect_w,'dd') between trunc(dt_referencia_p,'dd') and trunc(dt_final_p,'dd')) and (trunc(dt_inicial_prospect_w,'dd') between trunc(dt_abertura_canal_w,'dd') and trunc(dt_fechamento_canal_w,'dd')) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION com_obter_se_prospect_ativo ( nr_seq_cliente_p bigint, nr_seq_canal_p bigint, dt_referencia_p timestamp, dt_final_p timestamp, ie_opcao_p text) FROM PUBLIC;

