CREATE OR REPLACE FUNCTION com_obter_se_tipo_faq_perfil ( nr_seq_tipo_faq_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w			bigint;
ie_liberado_w			varchar(1) := 'S';

BEGIN

select	count(*)
into STRICT	qt_registro_w
from	com_tipo_faq_perfil
where	nr_seq_tipo_faq	= nr_seq_tipo_faq_p;

if (qt_registro_w > 0) then

	ie_liberado_w	:= 'N';

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_liberado_w
	from	com_tipo_faq_perfil
	where	nr_seq_tipo_faq = nr_seq_tipo_faq_p
	and	cd_perfil	= cd_perfil_p;

end if;

return ie_liberado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
