SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_obter_software_sd (nr_seq_solic_p bigint) RETURNS varchar AS $body$
DECLARE


ds_software_w	varchar(255);
ds_aplicacao_w	varchar(255);


C01 CURSOR FOR
	SELECT	substr(b.ds_aplicacao,1,255)
	from	com_solic_soft a,
		aplicacao_software b
	where	a.nr_seq_software = b.nr_sequencia
	and	a.nr_seq_solic = nr_seq_solic_p
	order by b.ds_aplicacao;

BEGIN

if (coalesce(nr_seq_solic_p,0) > 0) then

	open C01;
	loop
	fetch C01 into
		ds_aplicacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(ds_software_w::text, '') = '') and (ds_aplicacao_w IS NOT NULL AND ds_aplicacao_w::text <> '') then
			ds_software_w := substr(ds_aplicacao_w,1,255);
		elsif (ds_software_w IS NOT NULL AND ds_software_w::text <> '') and (ds_aplicacao_w IS NOT NULL AND ds_aplicacao_w::text <> '') then
			ds_software_w := ds_software_w || ', ' || ds_aplicacao_w;
		end if;

		end;
	end loop;
	close C01;
end if;

return	ds_software_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION com_obter_software_sd (nr_seq_solic_p bigint) FROM PUBLIC;

