SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION com_obter_vl_atual_neg ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
L - Valor atual LUT
C - Valor atual CDU
M - Valor atual Manutenção
*/
vl_atual_w	double precision;


BEGIN

if (ie_opcao_p = 'L') then
	select	coalesce(sum(a.vl_atual),0)
	into STRICT	vl_atual_w
	from	com_modalidade_lic b,
		com_cli_neg_lic_item a
	where	a.nr_seq_mod_licenc = b.nr_sequencia
	and	b.ie_modalidade = 'LUT'
	and	a.nr_seq_neg_lic = nr_sequencia_p;
elsif (ie_opcao_p = 'C') then
	select	coalesce(sum(a.vl_atual),0)
	into STRICT	vl_atual_w
	from	com_modalidade_lic b,
		com_cli_neg_lic_item a
	where	a.nr_seq_mod_licenc = b.nr_sequencia
	and	b.ie_modalidade = 'CDU'
	and	a.nr_seq_neg_lic = nr_sequencia_p;
elsif (ie_opcao_p = 'M') then
	select	coalesce(sum(a.vl_manut_mensal),0)
	into STRICT	vl_atual_w
	from	com_modalidade_lic b,
		com_cli_neg_lic_item a
	where	a.nr_seq_mod_licenc = b.nr_sequencia
	and	b.ie_modalidade = 'CDU'
	and	a.nr_seq_neg_lic = nr_sequencia_p;
end if;

return	vl_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
