CREATE OR REPLACE FUNCTION concatena_emails_certificado (cd_cnpj_p text ) RETURNS varchar AS $body$
DECLARE


ds_result_w		varchar(4000);
ds_email_w		varchar(255);

c01 CURSOR FOR
	SELECT  DS_EMAIL
	FROM 	TRE_ENVIO_CERTIFICADO_PJ
	WHERE 	cd_cnpj = cd_cnpj_p;


BEGIN
open c01;
loop
fetch c01 into	ds_email_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		if (ds_email_w IS NOT NULL AND ds_email_w::text <> '') then

			ds_result_w := ds_result_w || ', ' || ds_email_w;

		end if;

	end;
end loop;
close c01;

return	ds_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
