SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION concatena_turnos_lote (nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_turnos_w			varchar(255);
dt_horario_w		prescr_mat_hor.dt_horario%type;
nr_seq_turno_w 		prescr_mat_hor.nr_seq_turno%type;

c01 CURSOR FOR
	SELECT	distinct
			a.nr_seq_turno,
			a.dt_horario
	from	prescr_mat_hor a
	where	a.nr_seq_lote = nr_seq_lote_p
	and		a.dt_horario = ( SELECT	min(x.dt_horario)
							from	prescr_mat_hor x
							where	x.nr_seq_lote = nr_seq_lote_p
							and		x.nr_seq_turno = a.nr_seq_turno)
	order by dt_horario;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_turno_w,
	dt_horario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	max(ds_turno)
	into STRICT 	ds_turnos_w
	from 	regra_turno_disp
	where 	nr_sequencia = nr_seq_turno_w;

	if (ds_turnos_w IS NOT NULL AND ds_turnos_w::text <> '') then
		ds_retorno_w := ds_retorno_w || ds_turnos_w || '/';
	end if;

	end;
end loop;
close c01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w)-1);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
