CREATE OR REPLACE FUNCTION consiste_agenda_sexo ( cd_pessoa_fisica_p text, cd_agenda_p bigint ) RETURNS varchar AS $body$
DECLARE

			 
ie_permite_w		varchar(1) := 'S';
ie_sexo_agenda_w	varchar(1);
ie_sexo_paciente_w	varchar(1);


BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then 
 
	select coalesce(max(ie_sexo_agenda),'A') 
	into STRICT	ie_sexo_agenda_w 
	from  agenda a 
	where  a.cd_agenda = cd_agenda_p;
	 
	select  coalesce(max(ie_sexo), ie_sexo_agenda_w) 
	into STRICT	 ie_sexo_paciente_w 
	from   pessoa_fisica b 
	where  b.cd_pessoa_fisica = cd_pessoa_fisica_p;
 
	if (ie_sexo_agenda_w <> 'A') and (ie_sexo_agenda_w <> ie_sexo_paciente_w) then 
		 
	ie_permite_w := 'N';
 
	end if;
 
end if;
 
 
return	ie_permite_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
