CREATE OR REPLACE FUNCTION consiste_aspecto_analisar_sae ( nr_prescricao_p bigint, cd_empresa_p bigint, nr_seq_modelo_p bigint, cd_setor_pac_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

retorno_w		varchar(4000) := 'N';
pendentes_w		varchar(4000);
todos_pendentes_w	varchar(4000);
vl_parametro_w		varchar(5);
total_pendentes_w	bigint := 0;

/*ie_opcao_p
	C = Consistir;
	I = Itens Consistir;*/
C01 CURSOR FOR
	SELECT	distinct a.ds_tipo_item||':'||b.ds_item
	FROM pe_item_tipo_item c, pe_tipo_item a, pe_item_examinar b
LEFT OUTER JOIN pe_sae_mod_item d ON (b.nr_sequencia = d.nr_seq_item)
WHERE a.cd_empresa	= cd_empresa_p and a.nr_sequencia	= c.nr_seq_tipo_item and b.nr_sequencia	= c.nr_seq_item  and ((nr_seq_modelo_p = 0) or (d.nr_seq_modelo = nr_seq_modelo_p)) and ((d.cd_setor_pac = cd_setor_pac_p) or (coalesce(d.cd_setor_pac::text, '') = '')) and b.nr_sequencia not in (	SELECT   distinct(nr_seq_item)
								from     pe_prescr_item_result
								where    nr_seq_prescr = nr_prescricao_p
								
union

								select  distinct(nr_seq_item)
								from    pe_result_item_diag b,
										pe_prescr_item_result a
								where   b.nr_seq_result = a.nr_seq_result
								and		nr_seq_prescr 	= nr_prescricao_p) order by 1;


BEGIN

select coalesce(obter_valor_param_usuario(-1004, 2, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento), 'N')
into STRICT   vl_parametro_w
;

if (vl_parametro_w = 'N') then
    retorno_w := 'S';
else

    open C01;
    loop
    fetch C01 into
        pendentes_w;
    EXIT WHEN NOT FOUND; /* apply on C01 */
        begin
            if (pendentes_w IS NOT NULL AND pendentes_w::text <> '') then
                todos_pendentes_w := substr(todos_pendentes_w||pendentes_w||';'||chr(10)||chr(13),1,3900);
                total_pendentes_w := total_pendentes_w + 1;
            end if;
        end;
    end loop;
    close C01;

    if (ie_opcao_p = 'V') then
        if (total_pendentes_w = 0) then
            retorno_w := 'S';
        end if;
    end if;

        if (ie_opcao_p = 'I') then
                retorno_w	:= todos_pendentes_w;
        end if;

end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
