CREATE OR REPLACE FUNCTION consiste_bloq_geracao_atend (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ie_bloqueia_w	varchar(1) := 'N';
qt_regra_w	bigint;

cd_procedencia_w	integer;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	agenda_regra_atend;

if (qt_regra_w > 0) then
	select	max(cd_procedencia)
	into STRICT	cd_procedencia_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	select	count(*)
	into STRICT	qt_regra_w
	from	agenda_regra_atend
	where	cd_procedencia = cd_procedencia_w;

	if (qt_regra_w > 0) then
		ie_bloqueia_w := 'S';
	end if;

end if;

return	ie_bloqueia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
