SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consiste_box_pa ( nr_sequencia_p bigint, qt_hora_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_sequencia_w		bigint;
nr_seq_local_pa_w	bigint;
ds_retorno_w		varchar(60);
nr_seq_grupo_w		bigint;
ie_controle_w		varchar(10)	:= 'I';


BEGIN 
 
select	max(nr_seq_grupo_pa) 
into STRICT	nr_seq_grupo_w 
from	pa_local 
where	nr_sequencia	= nr_sequencia_p;
 
if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then 
	select	coalesce(max(IE_CONTROLE),'I') 
	into STRICT	ie_controle_w 
	from	PA_GRUPO_LOCAL 
	where	nr_sequencia	= nr_seq_grupo_w;
end if;
 
 
if (ie_controle_w	= 'I') then 
 
	select	count(nr_seq_local_pa) 
	into STRICT	nr_seq_local_pa_w 
	from 	atendimento_ps_v b 
	where	b.nr_seq_local_pa	= nr_sequencia_p 
	and	dt_entrada between (clock_timestamp() - (qt_hora_p / 24)) and clock_timestamp() 
	and 	dt_alta_interno = to_date('30/12/2999','dd/mm/yyyy') 
	and (b.cd_classif_setor = '1');
 
end if;
 
if (nr_seq_local_pa_w > 0 ) and (ie_controle_w	= 'I')then 
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(306267); --'Este Box já está sendo utilizado por outro paciente!'; 
else	 
	ds_retorno_w	:= '';
 
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION consiste_box_pa ( nr_sequencia_p bigint, qt_hora_p bigint) FROM PUBLIC;

