CREATE OR REPLACE FUNCTION consiste_cancelamento_conta ( cd_perfil_p bigint, cd_convenio_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'S';
qt_existe_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	conv_regra_cancel_conta
where	cd_convenio = cd_convenio_p;

if (qt_existe_w > 0) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	conv_regra_cancel_conta
	where	((coalesce(cd_perfil::text, '') = '') or coalesce(cd_perfil, coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0))
	and	((coalesce(nm_usuario_regra::text, '') = '') or coalesce(nm_usuario_regra, coalesce(nm_usuario_p,'')) = coalesce(nm_usuario_p,''))
	and	cd_convenio = cd_convenio_p
	and	ie_permite_cancelar = 'S'
	and	ie_situacao = 'A';

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
