SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consiste_cartao_eme ( nr_seq_cartao_p bigint, cd_setor_atendimento_p bigint, cd_unidade_basica_p bigint, ie_opcao text) RETURNS bigint AS $body$
DECLARE

/*ie_opcao
P - Pendente
PS - Pendente Setor*/
ds_retorno_w			bigint;
nr_cartao_pendente_w		bigint;
nr_cartao_pend_setor_w		bigint;


BEGIN
if (ie_opcao = 'P') then
	begin
	select count(*)
	into STRICT	nr_cartao_pendente_w
	from	sup_lanc_cartao
	where	nr_seq_cartao = nr_seq_cartao_p
	and	coalesce(dt_liberacao::text, '') = ''
	and	coalesce(nr_atendimento::text, '') = '';

	ds_retorno_w := nr_cartao_pendente_w;
	end;
elsif (ie_opcao = 'PS') then
	begin
	select count(*)
	into STRICT	nr_cartao_pend_setor_w
	from	sup_lanc_cartao
	where	coalesce(nr_atendimento::text, '') = ''
	and	cd_setor_atendimento = cd_setor_atendimento_p
	and	cd_unidade_basica = cd_unidade_basica_p;

	ds_retorno_w := nr_cartao_pend_setor_w;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
