CREATE OR REPLACE FUNCTION consiste_carteira_atend (nr_atendimento_p bigint, ie_opcao_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_convenio_w		integer;
cd_usuario_convenio_w	varchar(30);
				

BEGIN	

select 	obter_convenio_atendimento(nr_atendimento_p)
into STRICT	cd_convenio_w
;

select	somente_numero(obter_dados_categ_conv(nr_atendimento_p, 'U'))
into STRICT	cd_usuario_convenio_w
;

-- Trazer mensagem ou forma de exibição da mensagem
if (coalesce(ie_opcao_p, '') = 'V') then
	ds_retorno_w:= Consiste_carteira_usuario(cd_convenio_w, cd_usuario_convenio_w, ie_opcao_p);
else
	ds_retorno_w:= Consiste_carteira_usuario(cd_convenio_w, cd_usuario_convenio_w);
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
