CREATE OR REPLACE FUNCTION consiste_carteira_usuario (cd_convenio_p text, cd_usuario_convenio_p text, ie_opcao_p text default null) RETURNS varchar AS $body$
DECLARE


qt_incial_w	integer;
qt_final_w	integer;
ds_mensagem_w	varchar(255);
codigo_w	regra_msg_codigo_usuario.cd_padrao%type;
ds_rertorno_w	varchar(255);
qt_existe_w	bigint;
i		bigint;
ds_formato_w	varchar(255);
ie_acho_w 	varchar(10) := 'N';
ie_bloqueia_w	regra_msg_codigo_usuario.cd_forma_aviso%type;


/*
- V  = IE_BLOQUEIA_W
- '' = Mensagem (Se não for passado parâmetro retorna a mensagem)
*/
C01 CURSOR FOR
	SELECT	qt_inicial,
		qt_final,
		ds_mensagem,
		cd_padrao,
		cd_forma_aviso
	from 	regra_msg_codigo_usuario
	where 	cd_convenio = cd_convenio_p;



BEGIN

select  count(*)
into STRICT	qt_existe_w
from    regra_msg_codigo_usuario;

if (coalesce(qt_existe_w,0) > 0) then

open C01;
loop
fetch C01 into
	qt_incial_w,
	qt_final_w,
	ds_mensagem_w,
	codigo_w,
	ie_bloqueia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_acho_w = 'N') then
		ds_formato_w := '';
		if (qt_incial_w IS NOT NULL AND qt_incial_w::text <> '') and (qt_final_w IS NOT NULL AND qt_final_w::text <> '')  then
			FOR  i IN qt_incial_w..qt_final_w LOOP
				ds_formato_w  := ds_formato_w || substr(cd_usuario_convenio_p, i, 1);
			end loop;

			if (ds_formato_w = codigo_w)  then
				ds_rertorno_w := substr(ds_mensagem_w,1,255);
				ie_acho_w := 'S';
				if (coalesce(ie_opcao_p, '') = 'V') then
					ds_rertorno_w := ie_bloqueia_w;
				end if;
			end if;
		end if;
	end if;
	end;
end loop;
close C01;

end if;

return	ds_rertorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
