CREATE OR REPLACE FUNCTION consiste_copia_prescr_rotina ( nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_exame_p bigint, ie_procedimento_p text, ie_exames_p text) RETURNS varchar AS $body$
DECLARE


ie_copia_w			varchar(1)	:= 'X';


BEGIN

if (ie_exames_p = 'S') and (coalesce(nr_seq_exame_p,0)	> 0) then
	ie_copia_w	:= 'S';
elsif (ie_procedimento_p = 'S') and (coalesce(nr_seq_exame_p,0)	= 0) then
	ie_copia_w	:= 'S';
else
	if (coalesce(nr_seq_proc_interno_p,0)	> 0) then
		select	coalesce(max(ie_copia),'X')
		into STRICT	ie_copia_w
		from	proc_interno
		where	nr_sequencia	= nr_seq_proc_interno_p;
	end if;

	if	((coalesce(nr_seq_proc_interno_p,0) = 0) or (ie_copia_w = 'X')) and (coalesce(cd_procedimento_p,0) > 0) then
		if	((ie_exames_p = 'C') and (coalesce(nr_seq_exame_p,0) > 0)) or
			((ie_procedimento_p = 'C') and (coalesce(nr_seq_exame_p,0) = 0)) then
			select coalesce(max(ie_copia),'N')
			into STRICT	ie_copia_w
			from	procedimento_prescricao
			where	cd_procedimento		= cd_procedimento_p
			and	ie_origem_proced	= ie_origem_proced_p;
		end if;
	end if;
end if;

return	ie_copia_w;

end;
$body$
