SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consiste_dev_item_adm ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, qt_devolucao_p bigint, nr_seq_processo_p bigint default null, nr_seq_lote_p bigint default null, nr_seq_lote_fornec_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_retorno_w		prescr_mat_hor.qt_dispensar_hor%type;
qt_devolucao_w		item_devolucao_material_pac.qt_material%type;
nr_seq_lote_ap_w	material_atend_paciente.nr_seq_lote_ap%type;
qt_retorno_lote_w	material_atend_paciente.qt_material%type;
qt_material_lote_w	material_atend_paciente.qt_material%type;
ie_lote_administrado_w varchar(1);
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
qt_material_conta_w	material_atend_paciente.qt_material%type;

C01 CURSOR FOR
	SELECT	a.nr_seq_lote_ap
	from	material_atend_paciente a
	where	1 = 1
	and		a.nr_atendimento = nr_atendimento_w
	and		a.nr_prescricao = nr_prescricao_p
	and		a.nr_seq_lote_fornec = nr_seq_lote_fornec_p
	and		coalesce(a.nr_devolucao::text, '') = ''
	order by a.nr_seq_lote_ap;
		

BEGIN

ie_lote_administrado_w := obter_param_usuario(42, 128, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_lote_administrado_w);

if (coalesce(nr_seq_prescricao_p,0) > 0) then

	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	select	coalesce(sum(qt_material),0)
	into STRICT	qt_devolucao_w
	from	item_devolucao_material_pac
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia_prescricao = nr_seq_prescricao_p
	and (coalesce(nr_seq_lote_p::text, '') = '' or nr_seq_lote = nr_seq_lote_p)
	and (coalesce(nr_seq_lote_fornec_p::text, '') = '' or coalesce(ie_lote_administrado_w, 'N') = 'S' or nr_seq_lote_fornec = nr_seq_lote_fornec_p);
	
	select	coalesce(sum(qt_dispensar_hor),0)
	into STRICT	qt_retorno_w
	from	prescr_mat_hor
	where	nr_prescricao = nr_prescricao_p
	and		nr_seq_material = nr_seq_prescricao_p
	and (coalesce(nr_seq_lote_p::text, '') = '' or nr_seq_lote = nr_seq_lote_p)
	and		(dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '')
	and		((coalesce(nr_seq_processo_p::text, '') = '') or (nr_seq_processo = nr_seq_processo_p));
	
	select coalesce(sum(a.qt_material),0)
	into STRICT qt_material_conta_w
	from material_atend_paciente a
	where a.nr_atendimento = nr_atendimento_w
	and a.nr_prescricao = nr_prescricao_p
	and	a.nr_sequencia_prescricao = nr_seq_prescricao_p
	and ((coalesce(nr_seq_lote_p::text, '') = '') or (a.nr_seq_lote_ap = nr_seq_lote_p))
	and (coalesce(nr_seq_lote_fornec_p::text, '') = '' or coalesce(ie_lote_administrado_w, 'N') = 'S' or nr_seq_lote_fornec = nr_seq_lote_fornec_p)
	and	coalesce(a.nr_devolucao::text, '') = '';
	
	if	((qt_devolucao_p + qt_devolucao_w) > (qt_material_conta_w - qt_retorno_w)) then
		ds_retorno_w := 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
