CREATE OR REPLACE FUNCTION consiste_dieta_lib (cd_dieta_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_dieta_w 	bigint;
ie_retorno_w 		varchar(1) := 'S';

BEGIN


Select count(*)
into STRICT qt_regra_dieta_w
from liberacao_dieta_eup
where coalesce(cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
and coalesce(cd_perfil,obter_perfil_ativo)  = obter_perfil_ativo;

if (qt_regra_dieta_w > 0) then

    select coalesce(max('S'), 'N')
    into STRICT ie_retorno_w
    from liberacao_dieta_eup
    where cd_dieta = cd_dieta_p
    and coalesce(cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
    and coalesce(cd_perfil,obter_perfil_ativo)  = obter_perfil_ativo;

end if;

return	ie_retorno_w;

end;
