CREATE OR REPLACE FUNCTION consiste_digito_assim ( ie_Rotina_p text, cd_codigo_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_resultado_w				boolean;
cd_digito_w				smallint;
ds_retorno_w				varchar(1);


BEGIN

/* Luiz F.M. - OS71251 - 16/10/2007 */

if (UPPER(ie_rotina_p) = 'ASSIM') then
	begin
	cd_digito_w		:= calcula_digito('Modulo11',substr(cd_codigo_p, 11, 10));
	ie_resultado_w		:= (substr(upper(cd_codigo_p),1,2) = 'AA') and (substr(cd_codigo_p,3,4) = substr(lpad(cd_estabelecimento_p,4,0),1,4)) and (substr(cd_codigo_p,7,4) = to_char(clock_timestamp(),'mmyy')) and (cd_digito_w = substr(cd_codigo_p, length(cd_codigo_p),1));
	end;
end if;
if (ie_resultado_w) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;
RETURN	ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
