CREATE OR REPLACE FUNCTION consiste_doacao_exame ( nr_seq_exame_p bigint, ds_resultado_p text) RETURNS varchar AS $body$
DECLARE


ie_sorologia_w	varchar(1);
qtd_w		integer;
ie_retorno_w	varchar(1) := 'N';


BEGIN

--verificar se o exame Ã© de sorologia
select	ie_sorologia
into STRICT	ie_sorologia_w
from   	san_exame
where 	nr_sequencia = nr_seq_exame_p
and		coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;

if (ie_sorologia_w = 'S') then
	--verificar se o exame  possui os dominios de  soro positivo ou reagente
	select	count(*)
	into STRICT	qtd_w
	from   	san_exame
	where  	nr_sequencia = nr_seq_exame_p
	and	coalesce(ie_consiste_result_pos,'S') = 'S'
	and    	cd_tipo_resultado in (1356,1357)
	and		coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;

	if (qtd_w > 0) and ((ds_resultado_p = obter_texto_tasy(803579, wheb_usuario_pck.get_nr_seq_idioma)) or (ds_resultado_p = obter_texto_tasy(309098, wheb_usuario_pck.get_nr_seq_idioma))
	or (ds_resultado_p = obter_texto_tasy(803579, wheb_usuario_pck.get_nr_seq_idioma)))then
		ie_retorno_w := 'S';
	end if;
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
