CREATE OR REPLACE FUNCTION consiste_dose_diferenciada ( ds_dose_diferenciada_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1)	:= 'S';
ds_validos_w			varchar(20) := '0123456789,-';
i				integer;
x				varchar(1);


BEGIN

if (ds_dose_diferenciada_p IS NOT NULL AND ds_dose_diferenciada_p::text <> '') then
	begin
	for i in 1..length(ds_dose_diferenciada_p) loop
		begin
		x	:= substr(ds_dose_diferenciada_p, i, 1);
		if (position(x in ds_validos_w) = 0) then
			ie_retorno_w	:= 'N';
			exit;
		end if;
		end;
	end loop;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
