CREATE OR REPLACE FUNCTION consiste_exec_proc_ama ( nr_seq_ama_p bigint) RETURNS varchar AS $body$
DECLARE


qt_proced_medic_amb_w	integer;
ds_mensagem_w		varchar(250) := '';


BEGIN

if (nr_seq_ama_p IS NOT NULL AND nr_seq_ama_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_proced_medic_amb_w
	from	proced_medico_ambulatorial
	where	coalesce(dt_exec_procedimento::text, '') = ''
	and     nr_seq_ama = nr_seq_ama_p;
	end;
end if;

if (qt_proced_medic_amb_w > 0) then
	ds_mensagem_w := substr(obter_texto_tasy(37250, wheb_usuario_pck.get_nr_seq_idioma),1,255);
end if;

return ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
