SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consiste_hora_suspensao ( nr_prescricao_p bigint, nm_usuario_p text, nr_seq_horario_p bigint, nr_seq_material_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE



--ie_tipo_p:
--I - Item
--H - horÃ¡rio
cd_estabelecimento_w	smallint;
ie_consistir_w		varchar(1) := 'N';
qt_tempo_susp_w		bigint;
qt_hora_aux_w		timestamp;
dt_horario_w		timestamp;


BEGIN

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

qt_tempo_susp_w := obter_param_usuario(924, 801, Obter_perfil_Ativo, nm_usuario_p, cd_estabelecimento_w, qt_tempo_susp_w);

if (ie_tipo_p = 'I') then

	select	min(dt_horario)
	into STRICT	dt_horario_w
	from	prescr_mat_hor
	where	nr_prescricao = nr_prescricao_p
	and	nr_seq_material = nr_seq_material_p
	and	dt_horario > clock_timestamp()
	and	coalesce(dt_fim_horario::text, '') = ''
	and	cd_motivo_baixa = 0
	and	coalesce(dt_suspensao::text, '') = '';

elsif (ie_tipo_p = 'H') then

	select	max(dt_horario)
	into STRICT	dt_horario_w
	from	prescr_mat_hor
	where	nr_sequencia = nr_seq_horario_p;

end if;

if (qt_tempo_susp_w > 0) then
	qt_hora_aux_w :=  trunc(dt_horario_w,'mi') - (qt_tempo_susp_w/60)/24;

	if (clock_timestamp() > qt_hora_aux_w) then
		ie_consistir_w := 'S';
	end if;
end if;

return	ie_consistir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
