SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consiste_liberacao_item_pront ( nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w			bigint;
cd_cgc_w			varchar(14);
ie_apresentar_w     varchar(1) := 'S';


BEGIN

if (  nr_seq_item_p = 1493) then

	cd_cgc_w	:= obter_cgc_estabelecimento(wheb_usuario_pck.get_cd_estabelecimento);

	select	count(*)
	into STRICT	qt_reg_w
	from	ITEM_PRONTUARIO_LIBERACAO
	where	nr_seq_item	= nr_seq_item_p
	and		CD_CGC_CLIENTE = cd_cgc_w
	and		clock_timestamp() between coalesce(DT_INICIO_VIGENCIA,clock_timestamp() - interval '1 days')  and  coalesce(DT_FIM_VIGENCIA,clock_timestamp() + interval '1 days');

	if (qt_reg_w	= 0) then

		ie_apresentar_w := 'N';

	end if;

end if;

return	ie_apresentar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
