CREATE OR REPLACE FUNCTION consiste_medico_cred_cirurgia (nr_cirurgia_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

						 
ie_credenciado_w	varchar(1) := 'N';
cd_convenio_w		bigint;
cd_categoria_w		varchar(10);
cd_setor_atendimento_w	integer;	
dt_referencia_w		timestamp;
nr_atendimento_w	bigint;
cd_especialidade_w	integer;
ie_tipo_atendimento_w	smallint;
cd_plano_w		varchar(100);
ie_possui_w		varchar(1);


BEGIN 
 
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_possui_w	 
from	medico_convenio 
where	cd_pessoa_fisica = cd_pessoa_fisica_p;
 
if (ie_possui_w = 'S') then 
	select	cd_convenio,						 
		cd_categoria, 
		cd_setor_atendimento, 
		coalesce(dt_inicio_real,dt_inicio_prevista), 
		nr_atendimento 
	into STRICT	cd_convenio_w, 
		cd_categoria_w, 
		cd_setor_atendimento_w, 
		dt_referencia_w, 
		nr_atendimento_w 
	from 	cirurgia 
	where	nr_cirurgia	= nr_cirurgia_p;
 
	ie_tipo_atendimento_w	:= Obter_Tipo_Atendimento(nr_atendimento_w);
	cd_especialidade_w	:= obter_especialidade_pf(cd_pessoa_fisica_p);
	cd_plano_w		:= Obter_Plano_Atendimento(nr_atendimento_w,'C');
 
	ie_credenciado_w := obter_se_medico_credenciado(wheb_usuario_pck.get_cd_estabelecimento,cd_pessoa_fisica_p,cd_convenio_w,null,cd_especialidade_w,cd_categoria_w,cd_setor_atendimento_w,cd_plano_w,dt_referencia_w,ie_tipo_atendimento_w, null, null);
end if;	
 
return	ie_credenciado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
