SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consiste_nr_conta_conv_prot ( nr_seq_protocolo_p bigint, nr_inicial_p bigint) RETURNS varchar AS $body$
DECLARE

					 
ie_retorno_w		varchar(15) := 'S';
qt_contas_w		bigint := 0;					
qt_exitente_w		bigint := 0;	
dt_mesano_referencia_w	protocolo_convenio.dt_mesano_referencia%type;
cd_convenio_w		protocolo_convenio.cd_convenio%type;


BEGIN 
 
select	count(*) 
into STRICT	qt_contas_w 
from	conta_paciente 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
 
begin 
select	max(dt_mesano_referencia), 
	max(cd_convenio) 
into STRICT	dt_mesano_referencia_w, 
	cd_convenio_w 
from	protocolo_convenio 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
exception 
when others then 
	dt_mesano_referencia_w	:= clock_timestamp();
	cd_convenio_w		:= null;
end;
 
begin 
select	count(*) 
into STRICT	qt_exitente_w 
from	conta_paciente 
where	(nr_conta_convenio IS NOT NULL AND nr_conta_convenio::text <> '') 
and	(nr_seq_protocolo IS NOT NULL AND nr_seq_protocolo::text <> '') 
and	nr_conta_convenio between nr_inicial_p and ((nr_inicial_p - 1) + qt_contas_w) 
and	trunc(dt_mesano_referencia,'month') = trunc(dt_mesano_referencia_w,'month') 
and	cd_convenio_parametro = coalesce(cd_convenio_w,cd_convenio_parametro) 
and	nr_seq_protocolo	<> nr_seq_protocolo_p;
exception 
when others then 
	qt_exitente_w := 0;
end;
 
if (qt_exitente_w > 0) then 
	ie_retorno_w	:= 'N';
else 
	ie_retorno_w	:= 'S';
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
