CREATE OR REPLACE FUNCTION consiste_perf_item_pront_templ ( cd_perfil_p bigint, nr_seq_item_pront_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 			varchar(1):='N';
qt_per_item_pront_templ_w	bigint := 0;

BEGIN

if (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') and (nr_seq_item_pront_p IS NOT NULL AND nr_seq_item_pront_p::text <> '') then
	begin

	select	count(*)
	into STRICT	qt_per_item_pront_templ_w
	from	perfil_item_pront a,
	        perfil_item_pront_template b
	where	a.nr_sequencia = b.nr_seq_item_perfil
	and	a.cd_perfil    = cd_perfil_p
	and	a.nr_seq_item_pront  = nr_seq_item_pront_p;


	if (qt_per_item_pront_templ_w > 0) then
		begin
		ds_retorno_w := 'S';
		end;
	else
		begin
		ds_retorno_w := 'N';
		end;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
