SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consiste_receb_ret_nf (nr_seq_retorno_p bigint, nr_seq_receb_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
count_nf_w		bigint;
nr_seq_nota_fiscal_w	nota_fiscal.nr_sequencia%type;

c01 CURSOR FOR 
SELECT	x.nr_seq_nota_fiscal 
from (--Notas das contas do retorno 
	SELECT	a.nr_sequencia nr_seq_nota_fiscal 
	from	nota_fiscal a, 
		convenio_retorno_item b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	b.nr_seq_retorno	= nr_seq_retorno_p 
	
union
 
	--Notas dos protocolos das contas do retorno 
	select	a.nr_sequencia nr_seq_nota_fiscal 
	from	nota_fiscal a, 
		convenio_retorno_item b, 
		conta_paciente c 
	where	c.nr_interno_conta	= b.nr_interno_conta 
	and	c.nr_seq_protocolo	= a.nr_seq_protocolo 
	and	b.nr_seq_retorno	= nr_seq_retorno_p 
	
union
 
	--Notas do lote de protocolo dos protocolos das contas do retorno 
	select	a.nr_sequencia nr_seq_nota_fiscal 
	from	nota_fiscal a, 
		convenio_retorno_item b, 
		conta_paciente c, 
		protocolo_convenio d 
	where	c.nr_seq_protocolo	= d.nr_seq_protocolo 
	and	c.nr_interno_conta	= b.nr_interno_conta 
	and	d.nr_seq_lote_protocolo	= a.nr_seq_lote_prot 
	and	b.nr_seq_retorno	= nr_seq_retorno_p) x 
where	1 = 1 
group by x.nr_seq_nota_fiscal;


BEGIN 
 
select	count(*) 
into STRICT	count_nf_w 
from	convenio_receb_nf 
where	nr_seq_receb	= nr_seq_receb_p;
 
if (count_nf_w > 0) then 
 
	open C01;
	loop 
	fetch C01 into 
		nr_seq_nota_fiscal_w;
	EXIT WHEN NOT FOUND or ds_retorno_w = 'S';  /* apply on C01 */
		begin 
 
		begin 
			select	'S' 
			into STRICT	ds_retorno_w 
			from	convenio_receb_nf 
			where	nr_seq_receb		= nr_seq_receb_p 
			and	nr_seq_nota_fiscal	<> nr_seq_nota_fiscal_w;
		exception 
		when others then 
			ds_retorno_w	:= 'N';
		end;
 
		end;
	end loop;
	close C01;
 
else 
	ds_retorno_w	:= 'N';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
