CREATE OR REPLACE FUNCTION consiste_se_consulta_evolucao ( cd_tipo_evolucao_p text, cd_pessoa_fisica_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


qt_evolucao_w		integer	:= 0;
ie_exibe_w		varchar(1)	:= 'N';

cd_perfil_w		bigint;
cd_pessoa_fisica_w	varchar(10);
ie_permite_consulta_w	varchar(10);
nm_usuario_w		varchar(30);
cd_estabelecimento_w	bigint;
C01 CURSOR FOR
	SELECT	coalesce(ie_permite_consulta,'S')
	from	tipo_evolucao_consulta
	where	cd_tipo_evolucao 					= cd_tipo_evolucao_p
	and	coalesce(cd_pessoa_fisica,	cd_pessoa_fisica_w)		= cd_pessoa_fisica_w
	and	coalesce(cd_perfil, 		cd_perfil_w) 			= cd_perfil_w
	and	coalesce(cd_estabelecimento,	cd_estabelecimento_w)		= cd_estabelecimento_w
	and	((coalesce(cd_setor_atendimento::text, '') = '') or (obter_se_setor_usuario(cd_setor_atendimento,nm_usuario_w) = 'S'))
	order by 	coalesce(cd_pessoa_fisica,0),
			coalesce(cd_perfil, 0),
			obter_se_setor_usuario(cd_setor_atendimento,nm_usuario_w);


BEGIN

nm_usuario_w		:= Obter_Usuario_Ativo;
cd_estabelecimento_w	:= coalesce(Obter_Estabelecimento_Ativo,0);

select	count(*)
into STRICT	qt_evolucao_w
from	tipo_evolucao_consulta b
where	cd_tipo_evolucao = cd_tipo_evolucao_p;

if (qt_evolucao_w	> 0) then
	begin


	cd_perfil_w 		:= coalesce(cd_perfil_p,0);
	cd_pessoa_fisica_w 	:= coalesce(cd_pessoa_fisica_p,'0');

	/*select	count(*)
	into	qt_evolucao_w
	from	tipo_evolucao_consulta
	where	cd_tipo_evolucao 					= cd_tipo_evolucao_p
	and	nvl(cd_pessoa_fisica,	cd_pessoa_fisica_w)		= cd_pessoa_fisica_w
	and	nvl(cd_perfil, 		cd_perfil_w) 			= cd_perfil_w;*/
	ie_permite_consulta_w	:= 'N';
	qt_evolucao_w	:= 0;
	open C01;
	loop
	fetch C01 into
		ie_permite_consulta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ie_permite_consulta_w	= 'S') then
			qt_evolucao_w	:= 1;
		end if;
		end;
	end loop;
	close C01;


	end;
else
	qt_evolucao_w := 1;
end if;

if (qt_evolucao_w > 0) then
	ie_exibe_w := 'S';
else
	ie_exibe_w := 'N';
end if;

return ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
