CREATE OR REPLACE FUNCTION consiste_se_exibe_alerta ( ie_tipo_alerta_p text, cd_pessoa_fisica_p text, cd_funcao_p text default null) RETURNS varchar AS $body$
DECLARE


qt_reg_w		integer	:= 0;
ie_exibe_w		varchar(1)	:= 'N';
cd_perfil_w		bigint;
ie_permite_acesso_w	varchar(10);
cd_funcao_ativo_w	integer;

C01 CURSOR FOR
	SELECT	coalesce(ie_permite,'S')
	from	tipo_alerta_liberacao
	where	ie_tipo_alerta 						= ie_tipo_alerta_p
	and	coalesce(cd_pessoa_fisica,	coalesce(cd_pessoa_fisica_p,0))	= coalesce(cd_pessoa_fisica_p,0)
	and	coalesce(cd_perfil, 		coalesce(cd_perfil_w,0)) 		= coalesce(cd_perfil_w,0)
	and	((coalesce(cd_funcao,		coalesce(cd_funcao_ativo_w,0))	= coalesce(cd_funcao_ativo_w,0) and coalesce(cd_funcao_ativo_w,0) <> 0)
		or coalesce(cd_funcao_ativo_w,0) = 0)
	order by 	coalesce(cd_pessoa_fisica,0),
			coalesce(cd_perfil, 0),
			coalesce(cd_funcao, 0);


BEGIN

cd_perfil_w	:= obter_perfil_ativo;

Select 	coalesce(cd_funcao_p,coalesce(wheb_usuario_pck.get_cd_funcao,0))
into STRICT	cd_funcao_ativo_w
;

select	count(*)
into STRICT	qt_reg_w
from	tipo_alerta_liberacao
where	ie_tipo_alerta 	= ie_tipo_alerta_p;

if (qt_reg_w	> 0) then
	begin

	ie_permite_acesso_w	:= 'N';
	open C01;
	loop
	fetch C01 into
		ie_permite_acesso_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

	if (ie_permite_acesso_w	= 'N') then
		qt_reg_w	:= 0;
	else
		qt_reg_w	:= 1;
	end if;

	end;
else
	qt_reg_w := 1;
end if;

if (qt_reg_w > 0) then
	ie_exibe_w := 'S';
else
	ie_exibe_w := 'N';
end if;

return ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
