CREATE OR REPLACE FUNCTION consiste_se_exibe_atend_amb ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
qt_evolucao_w		integer	:= 0;
ie_exibe_w		varchar(1)	:= 'N';
cd_perfil_w		bigint;
ie_permite_acesso_w	varchar(10);
cd_setor_atendimento_w	bigint;
cd_setor_atend_w	bigint	:= 0;
cd_pessoa_fisica_w	varchar(10);
nm_usuario_w		varchar(20);

C01 CURSOR FOR 
	SELECT	coalesce(ie_permite_acesso,'S') 
	from	TIPO_ATEND_AMB_REGRA 
	where	nr_seq_tipo_atend	= nr_sequencia_p 
	and		coalesce(cd_perfil, 		coalesce(cd_perfil_w,0)) 		= coalesce(cd_perfil_w,0) 
	and		coalesce(cd_pessoa_fisica,cd_pessoa_fisica_w)		= cd_pessoa_fisica_w 
	and		((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_pessoa_fisica_w,cd_especialidade) = 'S')) 
	order by 	coalesce(cd_pessoa_fisica,0), 
			coalesce(cd_perfil, 0), 
			coalesce(cd_especialidade,0);


BEGIN 
 
cd_perfil_w	:= obter_perfil_ativo;
cd_setor_atendimento_w	:= coalesce(wheb_usuario_pck.get_cd_setor_atendimento,0);
nm_usuario_w			:= coalesce(nm_usuario_p,wheb_usuario_pck.get_nm_usuario);
select	coalesce(max(cd_pessoa_fisica),'0') 
into STRICT	cd_pessoa_fisica_w 
from	usuario 
where	nm_usuario	= nm_usuario_w;
 
 
 
 
select	count(*) 
into STRICT	qt_evolucao_w 
from	tipo_atend_amb_regra a 
where	nr_seq_tipo_atend	= nr_sequencia_p;
 
 
if (qt_evolucao_w	> 0) then 
	begin 
	/* 
	select	count(*) 
	into	qt_evolucao_w 
	from	tipo_evolucao_acesso 
	where	cd_tipo_evolucao 					= cd_tipo_evolucao_p 
	and	nvl(cd_pessoa_fisica,	nvl(cd_pessoa_fisica_p,0))	= nvl(cd_pessoa_fisica_p,0) 
	and	nvl(cd_perfil, 		nvl(cd_perfil_w,0)) 		= nvl(cd_perfil_w,0);*/
 
	 
	ie_permite_acesso_w	:= 'N';
	open C01;
	loop 
	fetch C01 into	 
		ie_permite_acesso_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
	 
	if (ie_permite_acesso_w	= 'N') then 
		qt_evolucao_w	:= 0;
	else 
		qt_evolucao_w	:= 1;
	end if;
	 
	end;
else 
	qt_evolucao_w := 1;
end if;
 
if (qt_evolucao_w > 0) then 
	ie_exibe_w := 'S';
else 
	ie_exibe_w := 'N';
end if;
 
return ie_exibe_w;
 
end;
$body$
