SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consiste_se_exibe_evolucao ( cd_tipo_evolucao_p text, cd_pessoa_fisica_p text, nr_atendimento_p bigint default null, cd_pf_paciente_p text default null) RETURNS varchar AS $body$
DECLARE


qt_evolucao_w		integer	:= 0;
ie_exibe_w		varchar(1)	:= 'N';
cd_perfil_w		bigint;
ie_permite_acesso_w	varchar(10);
cd_setor_atendimento_w	bigint;
cd_setor_atend_w	bigint	:= 0;
ie_tipo_evolucao_w	varchar(3);
cd_estabelecimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);

C01 CURSOR FOR
	SELECT	coalesce(ie_permite_acesso,'S')
	from	tipo_evolucao_acesso
	where	cd_tipo_evolucao 					= cd_tipo_evolucao_p
	and	coalesce(cd_pessoa_fisica,	coalesce(cd_pessoa_fisica_w,0))	= coalesce(cd_pessoa_fisica_w,0)
	and	coalesce(cd_perfil, 		coalesce(cd_perfil_w,0)) 		= coalesce(cd_perfil_w,0)
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w)	= cd_setor_atendimento_w
	and	coalesce(cd_setor_paciente,cd_setor_atend_w)			= cd_setor_atend_w
	and	coalesce(ie_tipo_evolucao,ie_tipo_evolucao_w)		= ie_tipo_evolucao_w
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w)		= cd_estabelecimento_w
	and		consiste_se_pertence_grupo(nr_seq_grupo_pac, cd_pf_paciente_p) = 'S'
	order by 	coalesce(cd_pessoa_fisica,0), coalesce(cd_perfil, 0),	coalesce(cd_setor_atendimento, 0);


BEGIN

cd_perfil_w		:= obter_perfil_ativo;
cd_setor_atendimento_w	:= coalesce(wheb_usuario_pck.get_cd_setor_atendimento,0);
cd_estabelecimento_w	:= coalesce(Obter_Estabelecimento_Ativo,0);
cd_pessoa_fisica_w	:= Obter_Pessoa_Fisica_Usuario(wheb_usuario_pck.get_nm_usuario,'C');

select	coalesce(max(ie_tipo_evolucao),'X')
into STRICT	ie_tipo_evolucao_w	
from	usuario
where	nm_usuario = wheb_usuario_pck.get_nm_usuario;

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p	> 0)then
	begin
	cd_setor_atend_w	:= coalesce(obter_setor_atendimento(nr_atendimento_p),0);
	exception
		when others then
		cd_setor_atend_w	:= 0;
	end;
end if;

select	count(*)
into STRICT	qt_evolucao_w
from	tipo_evolucao_acesso b,
	tipo_evolucao a
where	a.cd_tipo_evolucao 	= b.cd_tipo_evolucao
and	a.cd_tipo_evolucao	= cd_tipo_evolucao_p
and	a.ie_situacao		= 'A';

if (qt_evolucao_w	> 0) then
	begin	
		ie_permite_acesso_w	:= 'N';
	open C01;
	loop
	fetch C01 into	
		ie_permite_acesso_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
	
	if (ie_permite_acesso_w	= 'N') then
		qt_evolucao_w	:= 0;
	else
		qt_evolucao_w	:= 1;
	end if;
	
	end;
else
	qt_evolucao_w := 1;
end if;

if (qt_evolucao_w > 0) then
	ie_exibe_w := 'S';
else
	ie_exibe_w := 'N';
end if;

return ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
