CREATE OR REPLACE FUNCTION consiste_se_pertence_grupo (nr_seq_grupo_pac_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE



ie_exibe_w			varchar(1) := 'N';
nr_seq_grupo_pac_w	bigint;
qt_vinculo_grupo_w	smallint;


BEGIN
	
	nr_seq_grupo_pac_w := coalesce(nr_seq_grupo_pac_p, 0);
	
	if (nr_seq_grupo_pac_w = 0) then
		ie_exibe_w := 'S';
		
	else

		select	count(*)
		into STRICT 	qt_vinculo_grupo_w
		from	pac_grupo_atend
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
		and     nr_seq_grupo = nr_seq_grupo_pac_w
		and     coalesce(dt_saida::text, '') = '';
	
		if (qt_vinculo_grupo_w > 0) then
			ie_exibe_w := 'S';
		
		end if;
	
	end if;

return ie_exibe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
