CREATE OR REPLACE FUNCTION consiste_se_setor_origem_dest ( cd_setor_atendimento_p bigint, ie_tipo_setor_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_tipo_setor_p - O(Origem), D(Destino)
*/
cd_setor_atendimento_w	bigint;
cd_perfil_w				bigint;
ds_retorno_w			varchar(1) := 'S';
nr_sequencia_w			bigint;
qt_regra_perfil_w		bigint;
qt_regra_setor_perf_w	bigint;


BEGIN
cd_setor_atendimento_w	:= cd_setor_atendimento_p;
cd_perfil_w				:= Obter_perfil_Ativo;

if (cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '') and (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '')then
	begin

	select	count(*)
	into STRICT	qt_regra_perfil_w
	from	agenda_regra_lib_setor
	where	cd_perfil	= cd_perfil_w
	and		((ie_tipo				= 	ie_tipo_setor_p) or (ie_tipo	= 'A'));

	if (qt_regra_perfil_w	> 0)then
		begin

		select	count(*)
		into STRICT	qt_regra_setor_perf_w
		from	agenda_regra_lib_setor
		where	cd_setor_atendimento	=	cd_setor_atendimento_w
		and		cd_perfil				=	cd_perfil_w
		and		((ie_tipo				= 	ie_tipo_setor_p) or (ie_tipo	= 'A'));

		if (qt_regra_setor_perf_w > 0)then
			ds_retorno_w	:= 'S';
		else
			ds_retorno_w	:= 'N';
		end if;

		end;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
