CREATE OR REPLACE FUNCTION consiste_subclassif_os ( nr_ordem_serv_p bigint ) RETURNS varchar AS $body$
DECLARE


mensagem_w		varchar(500);
nr_seq_classif_w	bigint;


BEGIN
