SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consiste_tit_bordero_rec (ds_titulos_p text) RETURNS varchar AS $body$
DECLARE


nr_titulo_w		titulo_receber.nr_titulo%type;
ds_lista_titulos_w	varchar(4000) := '';
ds_titulos_w		varchar(4000) := null;						
					
C01 CURSOR FOR
	SELECT	a.nr_titulo
	from	titulo_receber a
	where	' ' || ds_titulos_w || ' ' like '% ' || a.nr_titulo || ' %'
	and		a.vl_saldo_titulo = 0;	
			
/*Essa Function e utilizada no select do objeto  de sequencia 440944, que por sua vez e usado dentro da procedure Selecionar_Titulos_imClick no Delphi e java.*/
					

BEGIN

if (ds_titulos_p IS NOT NULL AND ds_titulos_p::text <> '') then

	ds_titulos_w := substr(replace(ds_titulos_p,',',' '),1,4000);

	open C01;
	loop
	fetch C01 into	
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (coalesce(ds_lista_titulos_w::text, '') = '') then
			ds_lista_titulos_w := nr_titulo_w;
		else
			ds_lista_titulos_w := substr(ds_lista_titulos_w || ',' ||nr_titulo_w,1,4000);
		end if;
		
		end;
	end loop;
	close C01;

end if;

return	ds_lista_titulos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION consiste_tit_bordero_rec (ds_titulos_p text) FROM PUBLIC;

