CREATE OR REPLACE FUNCTION consiste_vigencia_autor_atend (nr_sequencia_autor_p bigint) RETURNS varchar AS $body$
DECLARE


nr_atendimento_w	bigint;
cd_convenio_w		bigint;
dt_inicio_vigencia_w	timestamp;
dt_final_vigencia_w	timestamp;
dt_inicio_vig_autor_w	timestamp;
dt_final_vig_autor_w	timestamp;
ds_erro_w		varchar(2000);


BEGIN



select	max(a.dt_inicio_vigencia),
	max(a.dt_final_vigencia),
	max(b.dt_inicio_vigencia),
	max(b.dt_fim_vigencia)
into STRICT	dt_inicio_vigencia_w,
	dt_final_vigencia_w,
	dt_inicio_vig_autor_w,
	dt_final_vig_autor_w
from	atend_categoria_convenio a,
	autorizacao_convenio b
where	b.nr_sequencia 		= nr_sequencia_autor_p
and   	a.nr_atendimento      	= b.nr_atendimento
and   	a.cd_convenio         	= b.cd_convenio
--and   	obter_atecaco_atendimento(b.nr_atendimento) = a.nr_seq_interno
and  	b.dt_inicio_vigencia  >= a.dt_inicio_vigencia and
	b.dt_inicio_vigencia <= coalesce(a.dt_final_vigencia,b.dt_inicio_vigencia);


if (dt_inicio_vigencia_w IS NOT NULL AND dt_inicio_vigencia_w::text <> '') then
	/* Já existe uma vigência neste período para o atendimento! ||chr(10)||
	Data início: ||to_char(dt_inicio_vig_autor_w,'dd/mm/yyyy hh24:mi:ss') ||chr(10)||
	Data fim: ||to_char(dt_final_vig_autor_w,'dd/mm/yyyy hh24:mi:ss');*/
	ds_erro_w := wheb_mensagem_pck.get_texto(313246,'DT_INICIO_VIG_AUTOR_W='||to_char(dt_inicio_vig_autor_w,'dd/mm/yyyy hh24:mi:ss')||
							';DT_FINAL_VIG_AUTOR_W='||to_char(dt_final_vig_autor_w,'dd/mm/yyyy hh24:mi:ss'));
end if;

return ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
