CREATE OR REPLACE FUNCTION consistir_adic_lista_espera ( cd_pessoa_fisica_p text, dt_desejada_p timestamp) RETURNS varchar AS $body$
DECLARE

 
ie_registro_lista_w	varchar(1);
ds_msg_w		varchar(255);


BEGIN 
 
select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  
into STRICT	ie_registro_lista_w 
from	agenda_lista_espera 
where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	dt_desejada	= dt_desejada_p 
and	ie_status_espera	= 'A';
 
if (ie_registro_lista_w = 'S') then 
	ds_msg_w	:= substr(obter_texto_tasy(49713, wheb_usuario_pck.get_nr_seq_idioma),1,255);
end if;
 
return	ds_msg_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
