CREATE OR REPLACE FUNCTION consistir_agenda_hor_bloq_serv (cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_bloqueio_w	bigint;
qt_agenda_w	bigint;
ie_bloqueio_w	varchar(1);


BEGIN
select	count(*)
into STRICT	qt_bloqueio_w
from	agenda_consulta
where	cd_agenda = cd_agenda_p
and	trunc(dt_agenda,'dd') = trunc(dt_agenda_p,'dd')
and	(dt_agenda_p between dt_agenda and dt_agenda + (nr_minuto_duracao/1440)
or	dt_agenda_p between dt_agenda and dt_agenda + (nr_minuto_duracao/1440))
and	ie_status_agenda = 'B';

select	count(*)
into STRICT	qt_agenda_w
from	agenda_consulta
where	cd_agenda = cd_agenda_p
and	(dt_agenda_p between dt_agenda and dt_agenda + (nr_minuto_duracao/1440)
or	dt_agenda_p between dt_agenda and dt_agenda + (nr_minuto_duracao/1440))
and	ie_status_agenda = 'L';

if (qt_agenda_w >= qt_bloqueio_w) then
	ie_bloqueio_w := 'N';
else
	ie_bloqueio_w := 'S';
end if;

return ie_bloqueio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
