CREATE OR REPLACE FUNCTION consistir_atend_biometria ( ie_tipo_atendimento_p bigint, ie_clinica_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1);
ie_consiste_w		varchar(1) := 'N';


BEGIN

	select	obter_se_pessoa_biometria(cd_pessoa_fisica_p)
	into STRICT	ie_permite_w
	;

	select	coalesce(max('S'),'N')
	into STRICT	ie_consiste_w
	from	regra_atend_consist_bio
	where	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p)	=	ie_tipo_atendimento_p
	and	((coalesce(ie_clinica,ie_clinica_p)	=	ie_clinica_p) or coalesce(ie_clinica_p::text, '') = '');

	if (ie_consiste_w = 'N') then
		ie_permite_w := 'S';
	end if;

	return ie_permite_w;

end;
$body$
