SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_cid_morf_topog ( cd_topografia_p text, cd_morfologia_p text, cd_cid10_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p:
TM = Topografia e Morfologia
CT = CID10 e Topografia
CTM = CID10, Topografia e Morfologia */
qt_itens_w		bigint;


BEGIN

if (ie_opcao_p = 'TM') or (ie_opcao_p = 'CTM') then
	select	count(*)
	into STRICT	qt_itens_w
	from	cido_topografia_morfologia c,
			cido_topografia b,
			cido_morfologia a
	where	c.cd_categoria_topografica	= b.cd_categoria
	and		b.cd_topografia				= cd_topografia_p
	and		c.cd_morfologia				= a.cd_morfologia
	and		c.cd_morfologia				= cd_morfologia_p
	and (ie_opcao_p = 'TM' OR	b.cd_cid10 = cd_cid10_p);
elsif (ie_opcao_p = 'CT') then
	select	count(*)
	into STRICT	qt_itens_w
	from	cido_topografia b
	where	b.cd_cid10		=	cd_cid10_p
	and		b.cd_topografia	=	cd_topografia_p;
end if;

if (qt_itens_w > 0) then
	return 'S';
else
	return 'N';
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
