CREATE OR REPLACE FUNCTION consistir_conta_etapa ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_paciente_etapa_w	varchar(255) := null;
ie_paciente_etapa_w	varchar(1) := 'S';
nr_interno_conta_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_atendimento = nr_atendimento_p;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	max(obter_conta_paciente_etapa(nr_interno_conta_w, 'S')) 
	into STRICT	ds_paciente_etapa_w 
	;
	 
	if (ds_paciente_etapa_w = 'N') then 
		ie_paciente_etapa_w := 'N';
	end if;
	 
	end;
end loop;
close C01;
 
return	ie_paciente_etapa_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
