SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_cpf_nacional_eup ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
ie_possui_cpf_w		varchar(1);
cd_nacionalidade_w	varchar(8);
ie_brasileiro_w		varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_possui_cpf_w
from	pessoa_fisica
where 	cd_pessoa_fisica = cd_pessoa_fisica_p
and	(nr_cpf IS NOT NULL AND nr_cpf::text <> '');

	if (ie_possui_cpf_w = 'S') then
		ie_retorno_w	:= 'S';
	elsif (ie_possui_cpf_w = 'N') then

		select 	max(cd_nacionalidade)
		into STRICT	cd_nacionalidade_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;

		select	coalesce(max(ie_brasileiro),'N')
		into STRICT	ie_brasileiro_w
		from	nacionalidade
		where	cd_nacionalidade = cd_nacionalidade_w;

		if (ie_brasileiro_w = 'S') then
			ie_retorno_w := 'N';
		else
			ie_retorno_w := 'S';
		end if;

	end if;

return 	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
