SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION consistir_data_regra ( cd_categoria_p text, dt_inicio_vigencia_p timestamp, ie_opcao_p text, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE

/*
C - edição amb
*/
ie_consiste_w		varchar(1) 	:= 'N';
qt_vigencia_maior_w	integer 	:= 0;


BEGIN

if (ie_opcao_p = 'C') then

	select	count(*)
	into STRICT	qt_vigencia_maior_w
	from	convenio_amb
	where	dt_inicio_vigencia > dt_inicio_vigencia_p
	and	cd_categoria	= cd_categoria_p
	and	cd_convenio	= cd_convenio_p
	and	ie_situacao	= 'A';
end if;

if (qt_vigencia_maior_w > 0) then
	ie_consiste_w := 'S';
end if;

return ie_consiste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
