CREATE OR REPLACE FUNCTION consistir_especialidade_leito ( CD_SETOR_ATENDIMENTO_P bigint, CD_UNIDADE_BASICA_P text, CD_UNIDADE_COMPL_P text, CD_MEDICO_P text) RETURNS varchar AS $body$
DECLARE

 
ds_especialidade_leito_w	varchar(2)	:= 'S';			
			 
			 

BEGIN 
select 	CASE WHEN COUNT(*)=0 THEN  'S'  ELSE 'N' END  
into STRICT	ds_especialidade_leito_w 
from 	unidade_especialidade 
where 	cd_setor_atendimento = cd_setor_atendimento_p 
and	cd_unidade_basica = cd_unidade_basica_p 
and	cd_unidade_compl = cd_unidade_compl_p 
and clock_timestamp() between coalesce(DT_INICIO_VIGENCIA, clock_timestamp() - interval '1 days'/1440) and coalesce(DT_FINAL_VIGENCIA, clock_timestamp() + interval '1 days'/1440);
 
IF (ds_especialidade_leito_w = 'N') THEN 
	SELECT CASE WHEN COUNT(*)=0 THEN  'N'  ELSE 'S' END  
	INTO STRICT  ds_especialidade_leito_w 
	FROM  unidade_especialidade a 
	WHERE cd_setor_atendimento = CD_SETOR_ATENDIMENTO_P 
	AND	  cd_unidade_basica 	= CD_UNIDADE_BASICA_P 
	AND	  cd_unidade_compl		= CD_UNIDADE_COMPL_P 
	AND EXISTS (SELECT 1 
				FROM 	medico_especialidade b 
				WHERE  cd_pessoa_fisica = CD_MEDICO_P 
				AND 	clock_timestamp() between coalesce(DT_INICIO_VIGENCIA, clock_timestamp() - interval '1 days'/1440) and coalesce(DT_FINAL_VIGENCIA, clock_timestamp() + interval '1 days'/1440) 
				AND		a.cd_especialidade = b.cd_especialidade);
end if;
 
return	ds_especialidade_leito_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
