CREATE OR REPLACE FUNCTION consistir_evt_relatorio_pepo ( nr_cirurgia_p bigint, nr_seq_evento_p bigint, cd_evento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_erro_w	varchar(1000) := '';
qt_evt_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_evt_w
from	evento_cirurgia_paciente
where	nr_cirurgia 	= nr_cirurgia_p
and	nr_seq_evento  	= nr_seq_evento_p;

if (qt_evt_w = 0) then
	begin
	ds_erro_w := 'O Evento '|| obter_desc_evento_cirurgia(cd_evento_p) ||' não foi registrado. Parâmetro [303].';
	end;
end if;

return ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
